local global = {}

---检测当前玩家拥有的卡牌中，是否包含有足够多的目标卡牌
---@param id number
---@param num number
function global.checkCardAndNumber(id, num)
    if not global.myCards or type(global.myCards) ~= "table" then
        return false
    end
    for i, v in ipairs(global.myCards) do
        if v[1] == id then
            if v[2] >= num then
                return true
            end
            return false
        end
    end
    return false
end

---@param cards table[]
function global.checkCards(cards)
    if not global.myCards or type(global.myCards) ~= "table" then
        return false
    end
    for i, v in ipairs(cards) do
        if not global.checkCardAndNumber(v[1], v[2]) then
            return false
        end
    end
    return true
end

return global